import Button from 'react-bootstrap/Button';
import { useState } from "react";
import Modal from 'react-bootstrap/Modal';
const ExpenseCreator=()=>{
  
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  function expenseForm() {
    return (
      <Form>
        <Form.Group className="mb-3" controlId="expenseDescription">
          <Form.Label>Expense Description</Form.Label>
          <Form.Control type="text" placeholder="Enter expense description" />
          
        </Form.Group>
  
        <Form.Group className="mb-3" controlId="payeeName">
          <Form.Label>payeeName</Form.Label>
          
    <Form.Select aria-label="Default select example">
      <option>Open this select menu</option>
      <option value="1">One</option>
      <option value="2">Two</option>
      <option value="3">Three</option>
    </Form.Select>
  
        </Form.Group>
        <Form.Group className="mb-3" controlId="Price">
          <Form.Check type="Price" label="Check me out" />
        </Form.Group>
        <Button variant="primary" type="submit">
          Submit
        </Button>
      </Form>
    );
  }

  return (
    <div>
      <Button variant="primary" onClick={handleShow} className='float-end'>
       Add New Expense
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Create New Expense</Modal.Title>
        </Modal.Header>
        <Modal.Body>
            {expenseForm()}
        </Modal.Body>
      </Modal>
      </div>
      )
   
 
}
export {ExpenseCreator}
